    public class VMMainWindow : CmpNotifyPropertyChanged
    {
        public VMMainWindow()
        {
            LoadMenu();
        }

        #region OBJETOS SECUNDARIOS

        #endregion

        #region Colección

        private ObservableCollection<MenuNode> _CollectionMenuNode;
        public ObservableCollection<MenuNode> CollectionMenuNode
        {
            get 
            {
                if (_CollectionMenuNode == null)
                    _CollectionMenuNode = new ObservableCollection<MenuNode>();
                return _CollectionMenuNode;
            }
            set 
            {
                _CollectionMenuNode = value;
                OnPropertyChanged("CollectionMenuNode");
            }
        }

        #endregion

        #region PROPERTY

        private bool _PropertyIsOpenPopupUser;
        public bool PropertyIsOpenPopupUser
        {
            get
            {
                return _PropertyIsOpenPopupUser;
            }
            set
            {
                _PropertyIsOpenPopupUser = value;
                OnPropertyChanged("PropertyIsOpenPopupUser");
            }
        }

        #endregion

        #region ICOMMAND

        public ICommand IOpenPopupUser
        {
            get
            {
                return CmpICommand.GetICommand((V) =>
                {
                    PropertyIsOpenPopupUser = true;
                });
            }
        }

        public ICommand IInformacionUsuario
        {
            get
            {
                return CmpICommand.GetICommand((T) =>
                {
                    try
                    {
                        new CmpNavigationService().Ir(new PGSGR_InformacionUsuario(), string.Empty);
                        PropertyIsOpenPopupUser = false;
                    }
                    catch (Exception)
                    {

                        throw;
                    }
                });
            }
        }

        #endregion

        #region MÉTODOS

        private void LoadMenu()
        {
            CollectionMenuNode.Add(GetMenuSeguridad());
            CollectionMenuNode.Add(GetMenuConfiguracion());
            //CollectionMenuNode.Add(GetMenuVentas());
            CollectionMenuNode.Add(GetMenuCaja());
        }

